  const server = new McpServer({
    name: "Firehydrant",
    version: "1.0.9",
  });

  const client = new FirehydrantCore({
    apiKey: deps.apiKey,
    serverURL: deps.serverURL,
    serverIdx: deps.serverIdx,
  });

